private void cmdNone_Click(object sender, RoutedEventArgs e)
{
foreach (CheckBox c in cboSettings.Items)
c.IsChecked = false;
foreach (CheckBox c in cboSettings.Items)
c.IsChecked = false;
}

private void cmdAll_Click(object sender, RoutedEventArgs e)
{
foreach (CheckBox c in cboSettings.Items)
c.IsChecked = true;
foreach (CheckBox c in cboSettings.Items)
c.IsChecked = true;
}

